package day1;

public class Program3_PrimitiveDatatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//boolean takes 1 bit
		boolean result = false;
		System.out.println("boolean:"+result);
		//byte 1 byte -128 to 127
		byte marks = 127;
		System.out.println("byte:"+marks);
		//char takes 2bytes
		char alpha = 'A';
		System.out.println("char:"+alpha);
		//short takes 2 bytes
		//-32000 to +32000
		short data = -32000;
		System.out.println("short:"+data);
	}

}
