package day1;

public class Program4_PrimitiveTypes2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//int takes 4 byte upto 9digits
		int num = 987654321;
		System.out.println("int:"+num);
		long phoneNumber = 9876543210987L;
		System.out.println("long:"+phoneNumber);
		float marks = 78.1234F;
		System.out.println("float:"+marks);
		double bankBalance = 7823.12344789;
		System.out.println("double:"+bankBalance);

	}

}
